const englishLink = document.querySelector(".english");
const malayLink = document.querySelector(".malay");

malayTxt = {
  instruction: `<p><strong>Cara penggunaan:</strong><br>
    1. Masukkan nama dan jumlah.<br>
    2. Tekan "+ Tambah nama" jika ingin menambah bilangan orang.<br>
    3. Masukkan peratusan cukai perkhidmatan dan caj perkhidmatan jika ada.<br>
    4. Tekan butang KIRA untuk mengira bil setiap orang termasuk cukai perkhidmatan & caj.
    </p>`,
  title: "Agihan Bayaran",
  addName: "+ Tambah Nama",
  serviceTaxIncluded: "Cukai Perkhidmatan (jika ada):",
  serviceCharge: "Caj Perkhidmatan (jika ada):",
  calculate: "KIRA",
  tableTitle: "KIRAAN INDIVIDU TERMASUK CUKAI & CAJ PERKHIDMATAN",
  cellTitle: "Nama",
  totalTxt: "JUMLAH"
};

englishTxt = {
  instruction: `<p><strong>Instruction:</strong><br>
    1. Enter name and amount.<br>
    2. Press "+ Add Name" if you want to add more people.<br>
    3. Enter service tax and service charge percent if any.<br>
    4. Press CALCULATE button to calculate each person's bill including service tax & charge.
    </p>`,
  title: "Split Bill",
  addName: "+ Add Name",
  serviceTaxIncluded: "Service Tax (if any):",
  serviceCharge: "Service Charge (if any):",
  calculate: "CALCULATE",
  tableTitle: "INDIVIDUAL CALCULATION INCLUDING SERVICE TAX & CHARGE",
  cellTitle: "Name",
  totalTxt: "TOTAL"
};

function changeLanguage(lang1, lang2, changeLang) {
  lang1.classList.add("lang-active");
  lang2.classList.remove("lang-active");

  document.querySelector(".instruction").innerHTML = changeLang.instruction;
  document.querySelector(".title").textContent = changeLang.title;
  document.querySelector(".add-people-link").textContent = changeLang.addName;
  document.querySelector(".service-tax p").textContent = changeLang.serviceTaxIncluded;
  document.querySelector(".service-charge p").textContent = changeLang.serviceCharge;
  document.querySelector(".btn").textContent = changeLang.calculate;
  document.querySelector(".table-title").textContent = changeLang.tableTitle;
  document.querySelector(".cell-title").textContent = changeLang.cellTitle;
  document.querySelector(".totalTxt").textContent = changeLang.totalTxt;
}

malayLink.addEventListener("click", () => {
  changeLanguage(malayLink, englishLink, malayTxt)
});

englishLink.addEventListener("click", () => {
  changeLanguage(englishLink, malayLink, englishTxt)
});